INSERT INTO toa_blog VALUES ('67','吴勇[政策法规处] 2014-07-23 工作日志','<p><span style=\"font-family:FangSong_GB2312;font-size:16px;\">1.修改《关于贯彻落实省委理论中心组</span><span style=\"font-family:FangSong_GB2312;font-size:16px;mso-hansi-font-family:方正小标宋简体;mso-bidi-font-family:方正小标宋简体;\">专题学习会精神有关情况<span style=\"font-family:FangSong_GB2312;color:black;font-size:16px;\">的报告<span lang=\"EN-US\"><?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /><o:p></o:p></span></span></span><span style=\"font-family:FangSong_GB2312;font-size:16px;\">》。</span></p>\r\n<p><span style=\"font-family:FangSong_GB2312;font-size:16px;\">2.参加刘维嘉副局长组织研究扶贫开发工作。</span></p>\r\n<p><span style=\"font-family:FangSong_GB2312;font-size:16px;\">3.研究起草落实“五大扶贫工程<span style=\"font-family:FangSong_GB2312;font-size:16px;\">”12项重点工作的意见。</span></span></p>','3','','2','2014-07-22','34');
