INSERT INTO toa_oalog VALUES ('2805','200','a:9:{s:5:\"title\";s:69:\"内江市召开扶贫开发建档立卡暨信息化建设工作会议\";s:7:\"content\";s:1427:\"<p style=\"line-height:30pt;text-indent:32.25pt;margin:0cm 0cm 0pt;layout-grid-mode:char;mso-line-height-rule:exactly;mso-layout-grid-align:none;\" class=\"MsoNormal\"><?xml:namespace prefix = st1 ns = \"urn:schemas-microsoft-com:office:smarttags\" /><st1:chsdate w:st=\"on\" year=\"2014\" month=\"6\" day=\"10\" islunardate=\"False\" isrocdate=\"False\"><span style=\"font-family:仿宋_GB2312;font-size:16pt;\" lang=\"EN-US\">2014</span><span style=\"font-family:仿宋_GB2312;font-size:16pt;\">年<span lang=\"EN-US\">6</span>月<span lang=\"EN-US\">10</span>日</span></st1:chsdate><span style=\"font-family:仿宋_GB2312;font-size:16pt;\">，全市扶贫开发建档立卡暨信息化建设工作会议在威远县银桦半岛酒店召开，会议由市政府副市长黄健主持，市委常委、政法委书记李发强出席会议并作重要讲话。参加会议的有：各县（区）党委或政府分管负责人、内江经开区分管负责人，各县（区）扶贫移民局局长<span lang=\"EN-US\">(</span>扶贫办主任<span lang=\"EN-US\">)</span>、财政局分管负责人，内江经开区财政局分管负责人。会议传达了全省扶贫开发建档立卡暨信息化建设工作会议精神，安排布署了全市扶贫开发建档立卡暨信息化建设工作。<span lang=\"EN-US\"><?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /><o:p></o:p></span></span></p>\";s:10:\"documentid\";s:3:\"0,5\";s:5:\"annex\";s:47:\"data/uploadfile/200/20140613/20140613030915.doc\";s:8:\"readuser\";s:12:\"全体人员\";s:4:\"type\";s:1:\"2\";s:3:\"uid\";s:3:\"200\";s:4:\"date\";s:19:\"2014-06-13 11:09:36\";s:3:\"key\";i:0;}','新增公共文件柜信息','2014-06-13 11:09:36','16','34');
