
DROP TABLE IF EXISTS toa_office_goods;
CREATE TABLE `toa_office_goods` (
  `id` int(10) NOT NULL AUTO_INCREMENT,
  `goods_type` varchar(255) DEFAULT NULL,
  `title` varchar(255) DEFAULT NULL,
  `specification` varchar(255) DEFAULT NULL,
  `unit` varchar(60) DEFAULT NULL,
  `price` varchar(60) DEFAULT NULL,
  `number` varchar(60) DEFAULT NULL,
  `content` text,
  `purchase` varchar(255) DEFAULT NULL,
  `examination` varchar(255) DEFAULT NULL,
  `warehousing` varchar(255) DEFAULT NULL,
  `date` datetime DEFAULT NULL,
  `uid` varchar(10) DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8;


DROP TABLE IF EXISTS toa_office_goods_key;
CREATE TABLE `toa_office_goods_key` (
  `id` int(8) NOT NULL AUTO_INCREMENT,
  `purchase` varchar(255) DEFAULT NULL,
  `examination` varchar(255) DEFAULT NULL,
  `warehousing` varchar(255) DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=2 DEFAULT CHARSET=utf8;


DROP TABLE IF EXISTS toa_office_goods_record;
CREATE TABLE `toa_office_goods_record` (
  `id` int(10) NOT NULL AUTO_INCREMENT,
  `goods_type` varchar(255) DEFAULT NULL,
  `title` varchar(255) DEFAULT NULL,
  `specification` varchar(255) DEFAULT NULL,
  `unit` varchar(50) DEFAULT NULL,
  `price` varchar(60) DEFAULT NULL,
  `number` varchar(50) DEFAULT NULL,
  `content` text,
  `recorduser` varchar(60) DEFAULT NULL,
  `department` varchar(10) DEFAULT NULL,
  `type` varchar(10) DEFAULT NULL,
  `officegoods` varchar(10) DEFAULT NULL,
  `date` datetime DEFAULT NULL,
  `uid` varchar(10) DEFAULT NULL,
  `recordnum` varchar(20) DEFAULT NULL,
  `examination` varchar(30) DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8;


DROP TABLE IF EXISTS toa_office_goods_stock;
CREATE TABLE `toa_office_goods_stock` (
  `id` int(10) NOT NULL AUTO_INCREMENT,
  `officegoods` varchar(10) DEFAULT NULL,
  `goods_type` varchar(255) DEFAULT NULL,
  `title` varchar(255) DEFAULT NULL,
  `specification` varchar(255) DEFAULT NULL,
  `unit` varchar(60) DEFAULT NULL,
  `price` varchar(60) DEFAULT NULL,
  `number` varchar(60) DEFAULT NULL,
  `content` text,
  `purchase` varchar(255) DEFAULT NULL,
  `type` varchar(10) DEFAULT NULL,
  `date` datetime DEFAULT NULL,
  `uid` varchar(10) DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8;


DROP TABLE IF EXISTS toa_office_type;
CREATE TABLE `toa_office_type` (
  `oid` int(10) NOT NULL AUTO_INCREMENT,
  `oname` varchar(255) DEFAULT NULL,
  `otype` varchar(10) DEFAULT NULL,
  `uid` varchar(10) DEFAULT NULL,
  PRIMARY KEY (`oid`)
) ENGINE=MyISAM AUTO_INCREMENT=85 DEFAULT CHARSET=utf8;


DROP TABLE IF EXISTS toa_online;
CREATE TABLE `toa_online` (
  `id` int(10) NOT NULL AUTO_INCREMENT,
  `startdate` datetime DEFAULT NULL,
  `uid` varchar(20) DEFAULT NULL,
  `enddate` datetime DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=196 DEFAULT CHARSET=utf8;


DROP TABLE IF EXISTS toa_personnel;
CREATE TABLE `toa_personnel` (
  `id` int(8) NOT NULL AUTO_INCREMENT,
  `type` varchar(5) DEFAULT NULL,
  `name` varchar(255) DEFAULT NULL,
  `uid` varchar(255) DEFAULT NULL,
  `designationdate` datetime DEFAULT NULL,
  `approvaldate` datetime DEFAULT NULL,
  `lnstructions` varchar(200) DEFAULT NULL,
  `node` varchar(20) DEFAULT NULL,
  `pkey` varchar(16) DEFAULT NULL,
  `fileid` varchar(16) DEFAULT NULL,
  `apptype` varchar(16) NOT NULL,
  `appflow` varchar(16) NOT NULL,
  `appkey` varchar(8) NOT NULL,
  `appkey1` varchar(8) NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=8450 DEFAULT CHARSET=utf8;

