INSERT INTO toa_oalog VALUES ('8522','200','a:9:{s:5:\"title\";s:72:\"内江市委书记彭宇行调研扶贫开发工作并慰问贫困群众\";s:7:\"content\";s:2447:\"<p style=\"line-height:23.45pt;text-indent:36pt;margin:0cm 0cm 0pt;mso-char-indent-count:2.25;\"><span style=\"font-family:宋体;\"><?xml:namespace prefix = st1 ns = \"urn:schemas-microsoft-com:office:smarttags\" /><st1:chsdate w:st=\"on\" year=\"2014\" month=\"7\" day=\"8\" islunardate=\"False\" isrocdate=\"False\"><span style=\"font-family:仿宋;font-size:16pt;\" lang=\"EN-US\">7</span><span style=\"font-family:仿宋;font-size:16pt;\">月<span lang=\"EN-US\">8</span>日</span></st1:chsdate><span style=\"font-family:仿宋;font-size:16pt;\">，内江<span style=\"color:#333333;\">市委书记彭宇行率市级有关部门负责人，深入东兴区双才镇阳坡村、大治乡土主村调研<span lang=\"EN-US\">2014</span>年贫困村项目建设及精准扶贫试点工作开展情况，并入户看望慰问了土主村刘洪清、李远彬、陈扬模等部分贫困群众。在土主村<span lang=\"EN-US\">2</span>社，彭书记与贫困户面对面交流，询问困难情况、了解帮扶需求、共商脱贫对策、探讨致富办法。<span lang=\"EN-US\"><?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /><o:p></o:p></span></span></span></span></p>\r\n<p style=\"line-height:23.45pt;text-indent:36pt;margin:0cm 0cm 0pt;mso-char-indent-count:2.25;\"><span style=\"font-family:仿宋;color:#333333;font-size:16pt;\"><span style=\"font-family:宋体;\">彭宇行对扶贫部门正在开展的扶贫项目建设和精准扶试点工作给予了充分肯定，强调扶贫开发工作要与当前正在开展的党的群众路线教育实践活动结合起来，从贫困群众的需求出发，因地制宜，一户一策，切实把党和政府的关怀送给最困难、最急需的群众。交通、住建、民政、农业、水务、卫生、残联等部门要对贫困群众提出的道路、住房、医疗、生活等方面的困难和问题具体研究，从项目、政策、资金的层面切实加以解决。<span lang=\"EN-US\"><o:p></o:p></span></span></span></p>\r\n<p style=\"line-height:23.45pt;text-indent:32pt;margin:0cm 0cm 0pt;mso-char-indent-count:2.0;\"><span style=\"font-family:仿宋;color:#333333;font-size:16pt;\"><span style=\"font-family:宋体;\">市委常委、政法委书记李发强，副市长黄健、市扶贫移民局局长杨迎春，东兴区委书记黄俊伟及相关部门负责人参加调研。<span lang=\"EN-US\"><o:p></o:p></span></span></span></p>\";s:10:\"documentid\";s:3:\"0,5\";s:5:\"annex\";s:47:\"data/uploadfile/200/20140710/20140710022501.doc\";s:8:\"readuser\";s:12:\"全体人员\";s:4:\"type\";s:1:\"2\";s:3:\"uid\";s:3:\"200\";s:4:\"date\";s:19:\"2014-07-10 10:25:54\";s:3:\"key\";i:0;}','新增公共文件柜信息','2014-07-10 10:25:54','47','34');
