INSERT INTO toa_oalog VALUES ('10708','2','a:12:{s:5:\"title\";s:33:\"7月21日至7月25日工作安排\";s:7:\"content\";s:1596:\"<p>7月21日，参加局务会，汇报全国扶贫办主任座谈会精神，按会议要求向省领导撰写情况报告和工作建议；</p>\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 送审张局长在省委农村工作领导小组会上大发言材料、半年工作总结及下半年打算；</p>\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 向省委办公厅报送四川省农村扶贫开发情况；向省政府办公厅报送上半年工作情况；</p>\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 召开处务会传达局务会精神，安排本周任务。</p>\r\n<p>7月22日，向局主要领导报送相关材料；</p>\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 协调到省外调研移民立法事项；</p>\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;报送局机关学习省委理论中心组学习情况；</p>\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;协调川台、发展网记者采访彝区、《扶贫条例》等事项；</p>\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;报告机关办公。</p>\r\n<p>7月23日，随张谷局长到省委参加农村工作领导小组会议；</p>\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;机关办公。</p>\r\n<p>7月24日至7月25日，随刘兵局长赴重庆市调研移民工作，处内工作由张兵主持。</p>\";s:9:\"startdate\";s:19:\"2014-07-22 08:30:00\";s:7:\"enddate\";s:19:\"2014-07-25 18:01:00\";s:5:\"otype\";s:0:\"\";s:10:\"department\";s:0:\"\";s:13:\"participation\";s:0:\"\";s:6:\"person\";s:0:\"\";s:4:\"note\";s:0:\"\";s:4:\"date\";s:19:\"2014-07-22 10:02:07\";s:4:\"type\";s:6:\"个人\";s:3:\"uid\";s:1:\"2\";}','新增工作计划','2014-07-22 10:02:07','15','12');
