INSERT INTO toa_document VALUES ('15','内江市开展精准扶贫调研工作','<p style=\"text-indent:36pt;margin:0cm 0cm 0pt;mso-char-indent-count:2.25;\" class=\"MsoNormal\"><?xml:namespace prefix = st1 ns = \"urn:schemas-microsoft-com:office:smarttags\" /><st1:chsdate w:st=\"on\" year=\"2014\" month=\"6\" day=\"11\" islunardate=\"False\" isrocdate=\"False\"><span style=\"font-family:仿宋_GB2312;font-size:16pt;\" lang=\"EN-US\">2014</span><span style=\"font-family:仿宋_GB2312;font-size:16pt;\">年<span lang=\"EN-US\">6</span>月<span lang=\"EN-US\">11</span>日</span></st1:chsdate><span style=\"font-family:仿宋_GB2312;font-size:16pt;\">，市委常委、政法委书记李发强带领市扶贫移民局和市财政局相关人员到资中县银山镇金紫铺村开展精准扶贫调研工作。调研组一行与县、镇、村、组四级干部进行了座谈，对村、组推选出的贫困户进行了核实。通过调研，探索开展精准识别贫困户的操作办法和有效措施，分析研究了贫困户的致贫原因和帮扶需求，明确了下一步的帮扶计划和工作思路。<span lang=\"EN-US\"><?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /><o:p></o:p></span></span></p>\r\n<p style=\"text-indent:36pt;margin:0cm 0cm 0pt;mso-char-indent-count:2.25;\" class=\"MsoNormal\"><span style=\"font-family:仿宋_GB2312;font-size:16pt;\">发强常委要求，市扶贫移民局要以资中县精准扶贫试点工作经验为基础，进一步完善调研方式和思路，全面收集相关数据和材料，针对问题提出解决办法，形成基本情况清楚、精准识别有效、帮扶措施可行的调研报告，为市委、市政府关于扶贫开发改革创新提供决策参考。<span lang=\"EN-US\"><o:p></o:p></span></span></p>','0,5','data/uploadfile/200/20140613/20140613030732.doc','0','2','200','2014-06-13 11:07:38','全体人员');
