INSERT INTO toa_oalog VALUES ('18582','73','a:12:{s:5:\"title\";s:18:\"一周工作计划\";s:7:\"content\";s:2018:\"<table style=\"width:100%;\" border=\"1\" cellspacing=\"0\" bordercolor=\"#000000\" cellpadding=\"2\">\r\n<tbody>\r\n<tr>\r\n<td>&nbsp;星期</td>\r\n<td>&nbsp;内容 &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</td>\r\n<td>&nbsp;随同人员</td>\r\n</tr>\r\n<tr>\r\n<td>&nbsp;周一</td>\r\n<td>&nbsp;1、审查各市州上报的第四季度人口动态管理情况；2、参加处务会，汇报下月工作安排。</td>\r\n<td>&nbsp;</td>\r\n</tr>\r\n<tr>\r\n<td>&nbsp;周二</td>\r\n<td>&nbsp;1、起草后扶政策存在的问题和建议材料提纲；2、继续审查各市州第四季度人口动态管理情况；3、对甘孜州扶贫移民局上报的白荣定等3人享受移民后期扶持政策的请示进行批……</td>\r\n<td>&nbsp;</td>\r\n</tr>\r\n<tr>\r\n<td>&nbsp;周三</td>\r\n<td>&nbsp;1、继续审查各市州第四季度人口动态管理情况；2、对高县扶贫移民局和财政局调整2015年后扶整村推进示范片建设项目请示进行批复。3、对雅安市瀑布沟电站461名瀑电移民纳入全省后扶政策范围进行审查；4、起草后扶政策存在的问题和建议材料提纲，</td>\r\n<td>&nbsp;</td>\r\n</tr>\r\n<tr>\r\n<td>&nbsp;周四</td>\r\n<td>&nbsp;1、继续审查各市州第四季人口动态管理情况，拟写商请财政厅拨付第四季度后扶资金的函件；2、对雅安瀑布沟电站461名移民纳入全省后扶政策范围进行批复。</td>\r\n<td>&nbsp;</td>\r\n</tr>\r\n<tr>\r\n<td>&nbsp;周五</td>\r\n<td>&nbsp;1、印发拨付第四季度后扶资金的函件；2、清理平昌县调整整村推进规划的项目、资金情况；3、清理邻水县调整整村推进规划的项目、资金情况 。</td>\r\n<td>&nbsp;</td>\r\n</tr>\r\n<tr>\r\n<td>&nbsp;周六</td>\r\n<td>&nbsp;</td>\r\n<td>&nbsp;</td>\r\n</tr>\r\n<tr>\r\n<td>&nbsp;周日</td>\r\n<td>&nbsp;</td>\r\n<td>&nbsp;</td>\r\n</tr>\r\n</tbody>\r\n</table>\";s:9:\"startdate\";s:19:\"2014-09-01 08:09:00\";s:7:\"enddate\";s:19:\"2014-09-05 18:01:00\";s:5:\"otype\";s:0:\"\";s:10:\"department\";s:0:\"\";s:13:\"participation\";s:6:\"王刚\";s:6:\"person\";s:6:\"都勤\";s:4:\"note\";s:0:\"\";s:4:\"date\";s:19:\"2014-09-02 10:26:37\";s:4:\"type\";s:6:\"个人\";s:3:\"uid\";s:2:\"73\";}','新增工作计划','2014-09-02 10:26:37','23','12');
