INSERT INTO toa_oalog VALUES ('18715','30','a:12:{s:5:\"title\";s:18:\"一周工作计划\";s:7:\"content\";s:1852:\"<table style=\"width:100%;\" bordercolor=\"#000000\" cellspacing=\"0\" cellpadding=\"2\" border=\"1\">\r\n<tbody>\r\n<tr>\r\n<td>&nbsp;星期</td>\r\n<td>&nbsp;内容 &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</td>\r\n<td>&nbsp;随同人员</td>\r\n</tr>\r\n<tr>\r\n<td>&nbsp;周一</td>\r\n<td>&nbsp;上午参加20次局务会、21次局长办公会，下午和大渡河公司移民部讨论大岗山、枕头坝一级水电站移民安置蓄水验收准备工作。</td>\r\n<td>&nbsp;侯毅、刘莉，左绪海</td>\r\n</tr>\r\n<tr>\r\n<td>&nbsp;周二</td>\r\n<td>&nbsp;上午参加积立副局长召集的省局规划安置阶段技术咨询审查专家服务费用的管理和使用问题的研究讨论，下午处理安置处正常业务工作。</td>\r\n<td>&nbsp;</td>\r\n</tr>\r\n<tr>\r\n<td>&nbsp;周三</td>\r\n<td>&nbsp;和刘莉同志对安谷独立评估工作细则的审查、工作的开展进行研究。对安谷、沙坪、枕头坝一级、双江口、巴拉等电站的年度计划完成情况进行分析研究，作出下步工作安排。</td>\r\n<td>&nbsp;刘莉、左绪海</td>\r\n</tr>\r\n<tr>\r\n<td>&nbsp;周四</td>\r\n<td>和侯毅同志对瀑布沟、大岗山、硬梁包、泸定、黄金坪、长河坝等电站的年度计划完成情况进行分析研究，做出下步工作安排。</td>\r\n<td>&nbsp;侯毅</td>\r\n</tr>\r\n<tr>\r\n<td>&nbsp;周五</td>\r\n<td>&nbsp;和全处人员研究下周工作计划，总结本工作的完成情况。</td>\r\n<td>&nbsp;蒋光文、刘伯庆、何成俊、侯毅、刘莉。</td>\r\n</tr>\r\n<tr>\r\n<td>&nbsp;周六</td>\r\n<td>&nbsp;</td>\r\n<td>&nbsp;</td>\r\n</tr>\r\n<tr>\r\n<td>&nbsp;周日</td>\r\n<td>&nbsp;</td>\r\n<td>&nbsp;</td>\r\n</tr>\r\n</tbody>\r\n</table>\";s:9:\"startdate\";s:19:\"2014-09-01 09:27:00\";s:7:\"enddate\";s:19:\"2014-09-05 18:01:00\";s:5:\"otype\";s:0:\"\";s:10:\"department\";s:0:\"\";s:13:\"participation\";s:0:\"\";s:6:\"person\";s:16:\"张谷,何智彬\";s:4:\"note\";s:0:\"\";s:4:\"date\";s:19:\"2014-09-02 13:57:56\";s:4:\"type\";s:6:\"个人\";s:3:\"uid\";s:2:\"30\";}','新增工作计划','2014-09-02 13:57:56','24','12');
