INSERT INTO toa_oalog VALUES ('18833','75','a:12:{s:5:\"title\";s:18:\"一周工作计划\";s:7:\"content\";s:1370:\"<table style=\"width:100%;\" border=\"1\" cellspacing=\"0\" bordercolor=\"#000000\" cellpadding=\"2\">\r\n<tbody>\r\n<tr>\r\n<td>&nbsp;星期</td>\r\n<td>&nbsp;内容 &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</td>\r\n<td>&nbsp;随同人员</td>\r\n</tr>\r\n<tr>\r\n<td>&nbsp;周一</td>\r\n<td>&nbsp;召开处务会和支部会，学习省委13号文件，研究9月重点工作。</td>\r\n<td>&nbsp;</td>\r\n</tr>\r\n<tr>\r\n<td>&nbsp;周二</td>\r\n<td>&nbsp;办理监测评估工作和省级分中心验收工作请示，其他交办工作。</td>\r\n<td>&nbsp;</td>\r\n</tr>\r\n<tr>\r\n<td>&nbsp;周三</td>\r\n<td>&nbsp;同绵阳市研究金峰水库上报水利部纳入后期扶持政策问题。</td>\r\n<td>&nbsp;</td>\r\n</tr>\r\n<tr>\r\n<td>&nbsp;周四</td>\r\n<td>&nbsp;准备张局长赴云南参加水利部座谈会材料，处理日常事务。</td>\r\n<td>&nbsp;</td>\r\n</tr>\r\n<tr>\r\n<td>&nbsp;周五</td>\r\n<td>&nbsp;召开省级分中心建设验收。同财政、发改委研究2015年分中心建设和2014年监测评估工作。</td>\r\n<td>&nbsp;</td>\r\n</tr>\r\n<tr>\r\n<td>&nbsp;周六</td>\r\n<td>&nbsp;</td>\r\n<td>&nbsp;</td>\r\n</tr>\r\n<tr>\r\n<td>&nbsp;周日</td>\r\n<td>&nbsp;</td>\r\n<td>&nbsp;</td>\r\n</tr>\r\n</tbody>\r\n</table>\";s:9:\"startdate\";s:19:\"2014-09-01 08:30:00\";s:7:\"enddate\";s:19:\"2014-09-05 17:59:00\";s:5:\"otype\";s:0:\"\";s:10:\"department\";s:15:\"移民后扶处\";s:13:\"participation\";s:19:\"张潇元,阳伟明\";s:6:\"person\";s:23:\"张谷,邹积立,都勤\";s:4:\"note\";s:0:\"\";s:4:\"date\";s:19:\"2014-09-02 16:48:42\";s:4:\"type\";s:6:\"部门\";s:3:\"uid\";s:2:\"75\";}','新增工作计划','2014-09-02 16:48:42','25','12');
