INSERT INTO toa_oalog VALUES ('19156','65','a:7:{s:5:\"title\";s:65:\"李抒玲[国际合作与社会扶贫处] 2014-09-03 工作日志\";s:7:\"content\";s:640:\"<p>1.列席参加局长专题会：主题为：四川爱心扶贫网建设和“扶贫日”四川活动工作方案讨论。</p>\r\n<p>2.参加社会扶贫处第7次处务会，传达局务会、局长办公会精神，讨论安排近期急需要完成的重要工作：含“扶贫日”四川活动实施方案、“扶贫日”活动方案综合组工作计划、省内定点扶贫总结暨驻村帮扶会议相关工作、小额信贷政策争取等工作。</p>\r\n<p>3.制定2012－2013年对口定点扶贫工作总结暨驻村帮工作会议工作计划及分工，送邦华副书记和正洪处长审示后发处内人员执行。</p>\";s:6:\"number\";i:0;s:4:\"user\";s:12:\"全部人员\";s:4:\"type\";s:1:\"2\";s:4:\"date\";s:10:\"2014-09-03\";s:3:\"uid\";s:2:\"65\";}','新建日志信息','2014-09-03 19:12:08','240','11');
