INSERT INTO toa_oalog VALUES ('19404','65','a:7:{s:5:\"title\";s:65:\"李抒玲[国际合作与社会扶贫处] 2014-09-04 工作日志\";s:7:\"content\";s:738:\"向省政府办公厅二处提交国务院扶贫办刘永富主任给魏宏省长的信函；草拟并向局领导呈报《国际合作与社会扶贫处 局机关党委办公室关于全省2012－2013年度对口定点扶贫工作总结暨干部驻村帮扶工作大会相关工作事项的请示》；草拟完善以省委办公厅、省政府办公厅名义召开以上会议的会议通知代拟稿；草拟完善以省扶贫开发领导小组名义对2012－2013年对口定点扶贫工作先进单位和第五批扶贫挂职干部先进个人的通报表扬代拟稿。草拟并向局领导呈报《四川省工商联 四川省扶贫和移民工作局关于推动民营企业参与新一轮扶贫开发的实施意见的请示》。\";s:6:\"number\";i:0;s:4:\"user\";s:12:\"全部人员\";s:4:\"type\";s:1:\"2\";s:4:\"date\";s:10:\"2014-09-04\";s:3:\"uid\";s:2:\"65\";}','新建日志信息','2014-09-04 19:44:31','243','11');
