INSERT INTO toa_oalog VALUES ('20012','150','a:9:{s:8:\"category\";i:0;s:7:\"receive\";s:47:\"凉山扶贫办,甘孜扶移局,阿坝扶移局\";s:7:\"subject\";s:109:\"关于补充上报《2013年浙江省对口帮扶四川藏区项目中期评估报告》相关数据的通知\";s:7:\"content\";s:2080:\"<p style=\"margin:0cm 0cm 0pt;\" class=\"MsoNormal\"><span style=\"font-family:仿宋;font-size:16pt;\">甘孜、阿坝州扶贫移民局、凉山州扶贫办：<span lang=\"EN-US\"><?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /><o:p></o:p></span></span></p>\r\n<p style=\"text-indent:32pt;margin:0cm 0cm 0pt;mso-char-indent-count:2.0;\" class=\"MsoNormal\"><span style=\"font-family:仿宋;font-size:16pt;\">张谷局长<span lang=\"EN-US\">9</span>月<span lang=\"EN-US\">3</span>日批示要求，将《<span lang=\"EN-US\">2013</span>年浙江省对口帮扶四川藏区项目中期评估报告》修改后正式上报省委书记、省长、常委和副省长及秘书长。为保证《报告》质量，客观反映<span lang=\"EN-US\">2013</span>年浙江援藏项目的绩效，省局专门邀请北京相关专家对《报告》进行了评审。根据专家要求，现需你们对项目实施前后的相关数据进一步补充完善（见附件）。请各局（办）领导高度重视，于<span lang=\"EN-US\">9</span>月<span lang=\"EN-US\">10</span>日下午<span lang=\"EN-US\">17:00</span>前将补充数据后的表格电子版报省扶贫开发中心。<span lang=\"EN-US\"><o:p></o:p></span></span></p>\r\n<p style=\"text-indent:32pt;margin:0cm 0cm 0pt;mso-char-indent-count:2.0;\" class=\"MsoNormal\"><span style=\"font-family:仿宋;font-size:16pt;\">联系人：钟路，电话：<span lang=\"EN-US\">028-86600755</span>，<span lang=\"EN-US\">13699007321</span>。<span lang=\"EN-US\"><o:p></o:p></span></span></p>\r\n<p style=\"text-indent:32pt;margin:0cm 0cm 0pt;mso-char-indent-count:2.0;\" class=\"MsoNormal\"><span style=\"font-family:仿宋;font-size:16pt;\">邮<span lang=\"EN-US\"><span style=\"mso-spacerun:yes;\">&nbsp; </span></span>箱：<span lang=\"EN-US\">7125323@qq.com<o:p></o:p></span></span></p>\r\n<p style=\"text-indent:32pt;margin:0cm 0cm 0pt;mso-char-indent-count:2.0;\" class=\"MsoNormal\"><span style=\"font-family:仿宋;font-size:16pt;\">专此通知。<span lang=\"EN-US\"><o:p></o:p></span></span></p>\";s:8:\"appendix\";s:47:\"data/uploadfile/150/20140909/20140909082928.doc\";s:4:\"type\";s:1:\"4\";s:4:\"date\";s:19:\"2014-09-09 16:31:00\";s:3:\"pic\";s:1:\"0\";s:3:\"uid\";s:3:\"150\";}','新增通知','2014-09-09 16:31:00','116','13');
