INSERT INTO toa_oalog VALUES ('20734','75','a:12:{s:5:\"title\";s:18:\"一周工作计划\";s:7:\"content\";s:933:\"<table style=\"width:100%;\" border=\"1\" cellspacing=\"0\" bordercolor=\"#000000\" cellpadding=\"2\">\r\n<tbody>\r\n<tr>\r\n<td>&nbsp;星期</td>\r\n<td>&nbsp;内容 &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</td>\r\n<td>&nbsp;随同人员</td>\r\n</tr>\r\n<tr>\r\n<td>&nbsp;周一</td>\r\n<td>&nbsp;</td>\r\n<td>&nbsp;</td>\r\n</tr>\r\n<tr>\r\n<td>&nbsp;周二</td>\r\n<td>&nbsp;</td>\r\n<td>&nbsp;</td>\r\n</tr>\r\n<tr>\r\n<td>&nbsp;周三</td>\r\n<td>&nbsp;</td>\r\n<td>&nbsp;</td>\r\n</tr>\r\n<tr>\r\n<td>&nbsp;周四</td>\r\n<td>&nbsp;</td>\r\n<td>&nbsp;</td>\r\n</tr>\r\n<tr>\r\n<td>&nbsp;周五</td>\r\n<td>&nbsp;</td>\r\n<td>&nbsp;</td>\r\n</tr>\r\n<tr>\r\n<td>&nbsp;周六</td>\r\n<td>&nbsp;</td>\r\n<td>&nbsp;</td>\r\n</tr>\r\n<tr>\r\n<td>&nbsp;周日</td>\r\n<td>&nbsp;</td>\r\n<td>&nbsp;</td>\r\n</tr>\r\n</tbody>\r\n</table>\";s:9:\"startdate\";s:19:\"2014-09-15 17:47:00\";s:7:\"enddate\";s:19:\"2014-09-19 17:47:00\";s:5:\"otype\";s:0:\"\";s:10:\"department\";s:15:\"移民后扶处\";s:13:\"participation\";s:66:\"张潇元,阳伟明,韦俊红,李剑波,彭静耘,王刚,张云梅\";s:6:\"person\";s:23:\"张谷,邹积立,都勤\";s:4:\"note\";s:1122:\"移民后期扶持处完成工作情况\r\n及下周工作打算\r\n（2014年9月12日）\r\n\r\n局领导：\r\n一、本周完成工作：\r\n1、完成全国大中型水库移民后期扶持管理四川省省级分中心建设软件系统验收工作，为下一步开展填报培训作好准备工作。\r\n2、完成三峡外迁移民安置竣工验收的启动和培训工作。\r\n3、配合水利部移民局开展对南充市南部县2014年实施移民后期扶持政策绩效的监测评估工作。\r\n4、完成对雅安市、广元市核定后期扶持人口请示的回复。\r\n二、下周工作：\r\n1、继续配合水利部移民局开展对南充市南部县、乐山市峨边县2014年实施移民后期扶持政策绩效的监测评估工作。\r\n2、编写《四川省大中型水库移民避险解困试点工作实施意见》，并为启动我省大中型水库移民避险解困试点工作作好准备工作。\r\n3、商财政厅提出部分资金的分配建议，拟报局党组审议。\r\n4、完成平昌县、邻水县调整移民后扶整村推进项目调整的现场踏勘。\r\n5、其他领导交办事项。\";s:4:\"date\";s:19:\"2014-09-12 17:51:59\";s:4:\"type\";s:6:\"部门\";s:3:\"uid\";s:2:\"75\";}','新增工作计划','2014-09-12 17:51:59','37','12');
