INSERT INTO toa_oalog VALUES ('22042','28','a:12:{s:5:\"title\";s:18:\"一周工作计划\";s:7:\"content\";s:1336:\"<table style=\"width:100%;\" bordercolor=\"#000000\" cellspacing=\"0\" cellpadding=\"2\" border=\"1\">\r\n<tbody>\r\n<tr>\r\n<td>&nbsp;星期</td>\r\n<td>&nbsp;内容 &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</td>\r\n<td>&nbsp;随同人员</td>\r\n</tr>\r\n<tr>\r\n<td>&nbsp;周一</td>\r\n<td>\r\n<p>&nbsp;1、上午参加局务会；</p>\r\n<p>2、下午召开编辑部会议：点评总结4期，研究选题5期。</p>\r\n<p>3、督促检查浙江对口我省藏区2年计划及6个专项计划基础材料编制报送工作。</p>\r\n</td>\r\n<td>&nbsp;</td>\r\n</tr>\r\n<tr>\r\n<td>&nbsp;周二</td>\r\n<td>\r\n<p>1、&nbsp;督促检查驻村帮扶会议宣传组工作开展情况。</p>\r\n<p>2、&nbsp;按分工做好“扶贫日”筹备工作。</p>\r\n</td>\r\n<td>&nbsp;</td>\r\n</tr>\r\n<tr>\r\n<td>&nbsp;周三</td>\r\n<td>继续推进上述工作。</td>\r\n<td>&nbsp;</td>\r\n</tr>\r\n<tr>\r\n<td>&nbsp;周四</td>\r\n<td>&nbsp;同上</td>\r\n<td>&nbsp;</td>\r\n</tr>\r\n<tr>\r\n<td>&nbsp;周五</td>\r\n<td>&nbsp;同上</td>\r\n<td>&nbsp;</td>\r\n</tr>\r\n<tr>\r\n<td>&nbsp;周六</td>\r\n<td>&nbsp;</td>\r\n<td>&nbsp;</td>\r\n</tr>\r\n<tr>\r\n<td>&nbsp;周日</td>\r\n<td>&nbsp;</td>\r\n<td>&nbsp;</td>\r\n</tr>\r\n</tbody>\r\n</table>\";s:9:\"startdate\";s:19:\"2014-09-22 10:15:00\";s:7:\"enddate\";s:19:\"2014-09-26 10:15:00\";s:5:\"otype\";s:0:\"\";s:10:\"department\";s:0:\"\";s:13:\"participation\";s:33:\"唐义,张兵,王正洪,李抒玲\";s:6:\"person\";s:6:\"张谷\";s:4:\"note\";s:0:\"\";s:4:\"date\";s:19:\"2014-09-19 10:40:47\";s:4:\"type\";s:6:\"个人\";s:3:\"uid\";s:2:\"28\";}','新增工作计划','2014-09-19 10:40:47','43','12');
