INSERT INTO toa_oalog VALUES ('22068','82','a:12:{s:5:\"title\";s:27:\"信访处本周工作计划\";s:7:\"content\";s:5937:\"<table style=\"width:100%;\" border=\"1\" cellspacing=\"0\" bordercolor=\"#000000\" cellpadding=\"2\">\r\n<tbody>\r\n<tr>\r\n<td>&nbsp;星期</td>\r\n<td>&nbsp;内容 &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</td>\r\n<td>&nbsp;随同人员</td>\r\n</tr>\r\n<tr>\r\n<td>&nbsp;周一</td>\r\n<td>&nbsp;</td>\r\n<td>&nbsp;</td>\r\n</tr>\r\n<tr>\r\n<td>&nbsp;周二</td>\r\n<td>&nbsp;</td>\r\n<td>&nbsp;</td>\r\n</tr>\r\n<tr>\r\n<td>&nbsp;周三</td>\r\n<td>&nbsp;</td>\r\n<td>&nbsp;</td>\r\n</tr>\r\n<tr>\r\n<td>&nbsp;周四</td>\r\n<td>&nbsp;</td>\r\n<td>&nbsp;</td>\r\n</tr>\r\n<tr>\r\n<td>&nbsp;周五</td>\r\n<td>&nbsp;</td>\r\n<td>&nbsp;</td>\r\n</tr>\r\n<tr>\r\n<td>&nbsp;周六</td>\r\n<td>&nbsp;</td>\r\n<td>&nbsp;</td>\r\n</tr>\r\n<tr>\r\n<td>&nbsp;周日</td>\r\n<td>&nbsp;</td>\r\n<td>&nbsp;</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p style=\"margin-top:0pt;margin-bottom:0pt;\" class=\"p0\"><span style=\"font-family:\'仿宋\';color:#000000;font-size:16pt;mso-spacerun:\'yes\';\">一、密切掌握稳定动态</span><span style=\"font-family:\'仿宋\';color:#000000;font-size:16pt;mso-spacerun:\'yes\';\">。</span><span style=\"font-family:\'仿宋\';color:#000000;font-size:16pt;mso-spacerun:\'yes\';\"><?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /><o:p></o:p></span></p>\r\n<p style=\"margin-top:0pt;margin-bottom:0pt;\" class=\"p0\"><span style=\"font-family:\'仿宋\';color:#000000;font-size:16pt;mso-spacerun:\'yes\';\">二、认真接</span><span style=\"font-family:\'仿宋\';color:#000000;font-size:16pt;mso-spacerun:\'yes\';\">待来信来</span><span style=\"font-family:\'仿宋\';color:#000000;font-size:16pt;mso-spacerun:\'yes\';\">访，及时</span><span style=\"font-family:\'仿宋\';color:#000000;font-size:16pt;mso-spacerun:\'yes\';\">梳</span><span style=\"font-family:\'仿宋\';color:#000000;font-size:16pt;mso-spacerun:\'yes\';\">理</span><span style=\"font-family:\'仿宋\';color:#000000;font-size:16pt;mso-spacerun:\'yes\';\">群众</span><span style=\"font-family:\'仿宋\';color:#000000;font-size:16pt;mso-spacerun:\'yes\';\">诉求</span><span style=\"font-family:\'仿宋\';color:#000000;font-size:16pt;mso-spacerun:\'yes\';\">并</span><span style=\"font-family:\'仿宋\';color:#000000;font-size:16pt;mso-spacerun:\'yes\';\">按程序办理</span><span style=\"font-family:\'仿宋\';color:#000000;font-size:16pt;mso-spacerun:\'yes\';\">。</span><span style=\"font-family:\'仿宋\';color:#000000;font-size:16pt;mso-spacerun:\'yes\';\"><o:p></o:p></span></p>\r\n<p style=\"margin-top:0pt;margin-bottom:0pt;\" class=\"p0\"><span style=\"font-family:\'仿宋\';color:#000000;font-size:16pt;mso-spacerun:\'yes\';\">三、</span><span style=\"font-family:\'仿宋\';color:#000000;font-size:16pt;mso-spacerun:\'yes\';\">研究汉源县突出问题</span><span style=\"font-family:\'仿宋\';color:#000000;font-size:16pt;mso-spacerun:\'yes\';\">化解方案</span><span style=\"font-family:\'仿宋\';color:#000000;font-size:16pt;mso-spacerun:\'yes\';\">并督促落实，继续督促</span><span style=\"font-family:\'仿宋\';color:#000000;font-size:16pt;mso-spacerun:\'yes\';\">屏山</span><span style=\"font-family:\'仿宋\';color:#000000;font-size:16pt;mso-spacerun:\'yes\';\">县</span><span style=\"font-family:\'仿宋\';color:#000000;font-size:16pt;mso-spacerun:\'yes\';\">、金阳</span><span style=\"font-family:\'仿宋\';color:#000000;font-size:16pt;mso-spacerun:\'yes\';\">县妥善处置和化解</span><span style=\"font-family:\'仿宋\';color:#000000;font-size:16pt;mso-spacerun:\'yes\';\">集访</span><span style=\"font-family:\'仿宋\';color:#000000;font-size:16pt;mso-spacerun:\'yes\';\">事件，及时报告有关情况。</span><span style=\"font-family:\'仿宋\';color:#000000;font-size:16pt;mso-spacerun:\'yes\';\"><o:p></o:p></span></p>\r\n<p style=\"margin-top:0pt;margin-bottom:0pt;\" class=\"p0\"><span style=\"font-family:\'仿宋\';color:#000000;font-size:16pt;mso-spacerun:\'yes\';\">四、</span><span style=\"font-family:\'仿宋\';color:#000000;font-size:16pt;mso-spacerun:\'yes\';\">印发信访突出问题</span><span style=\"font-family:\'仿宋\';color:#000000;font-size:16pt;mso-spacerun:\'yes\';\">挂牌督办</span><span style=\"font-family:\'仿宋\';color:#000000;font-size:16pt;mso-spacerun:\'yes\';\">包案化解方案。</span><span style=\"font-family:\'仿宋\';color:#000000;font-size:16pt;mso-spacerun:\'yes\';\"><o:p></o:p></span></p>\r\n<p style=\"margin-top:0pt;margin-bottom:0pt;\" class=\"p0\"><span style=\"font-family:\'仿宋\';color:#000000;font-size:16pt;mso-spacerun:\'yes\';\">五、到相关厅局学习信访三级终结经验做法。</span><span style=\"font-family:\'仿宋\';color:#000000;font-size:16pt;mso-spacerun:\'yes\';\"><o:p></o:p></span></p>\r\n<p style=\"margin-top:0pt;margin-bottom:0pt;\" class=\"p0\"><span style=\"font-family:\'仿宋\';color:#000000;font-size:16pt;mso-spacerun:\'yes\';\">七</span><span style=\"font-family:\'仿宋\';color:#000000;font-size:16pt;mso-spacerun:\'yes\';\">、复核</span><span style=\"font-family:\'仿宋\';color:#000000;font-size:16pt;mso-spacerun:\'yes\';\">二滩、溪洛渡电站移民信访事项。</span><span style=\"font-family:\'仿宋\';color:#000000;font-size:16pt;mso-spacerun:\'yes\';\"><o:p></o:p></span></p>\r\n<p style=\"margin-top:0pt;margin-bottom:0pt;\" class=\"p0\"><span style=\"font-family:\'仿宋\';color:#000000;font-size:16pt;mso-spacerun:\'yes\';\">八</span><span style=\"font-family:\'仿宋\';color:#000000;font-size:16pt;mso-spacerun:\'yes\';\">、完成</span><span style=\"font-family:\'仿宋\';color:#000000;font-size:16pt;mso-spacerun:\'yes\';\">局领导和上级机关交办工作。</span><span style=\"font-family:\'仿宋\';font-size:16pt;mso-spacerun:\'yes\';\"><o:p></o:p></span></p>\r\n<!--EndFragment-->\";s:9:\"startdate\";s:19:\"2014-09-22 08:30:00\";s:7:\"enddate\";s:19:\"2014-09-26 18:01:00\";s:5:\"otype\";s:0:\"\";s:10:\"department\";s:0:\"\";s:13:\"participation\";s:50:\"姚立,兰素华,黄代利,曾磊,朱敏,赵燕玲\";s:6:\"person\";s:16:\"张谷,何智彬\";s:4:\"note\";s:0:\"\";s:4:\"date\";s:19:\"2014-09-19 11:34:39\";s:4:\"type\";s:6:\"个人\";s:3:\"uid\";s:2:\"82\";}','新增工作计划','2014-09-19 11:34:39','44','12');
