INSERT INTO toa_oalog VALUES ('23173','34','a:7:{s:5:\"title\";s:47:\"吴勇[政策法规处] 2014-09-24 工作日志\";s:7:\"content\";s:312:\"<p>1.修改贯彻国务院稳增长促改革调结构惠民生政策措施落实情况的自查报告，发送相关业务处室征求意见。</p>\r\n<p>2.参加遂宁市政府对接工作会。</p>\r\n<p>3.参加发改委收入分配改革会议。</p>\r\n<p>4.加班修改“扶贫日”活动协调会资料。</p>\";s:6:\"number\";i:0;s:4:\"user\";s:12:\"全部人员\";s:4:\"type\";s:1:\"2\";s:4:\"date\";s:10:\"2014-09-19\";s:3:\"uid\";s:2:\"34\";}','新建日志信息','2014-09-24 11:45:15','301','11');
