INSERT INTO toa_oalog VALUES ('24550','82','a:12:{s:5:\"title\";s:50:\"信访处每周工作计划（10月8日?17日）\";s:7:\"content\";s:2842:\"<table style=\"width:100%;\" border=\"1\" cellspacing=\"0\" bordercolor=\"#000000\" cellpadding=\"2\">\r\n<tbody>\r\n<tr>\r\n<td>&nbsp;星期</td>\r\n<td>&nbsp;内容 &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</td>\r\n<td>&nbsp;随同人员</td>\r\n</tr>\r\n<tr>\r\n<td>&nbsp;周一</td>\r\n<td>&nbsp;</td>\r\n<td>&nbsp;</td>\r\n</tr>\r\n<tr>\r\n<td>&nbsp;周二</td>\r\n<td>&nbsp;</td>\r\n<td>&nbsp;</td>\r\n</tr>\r\n<tr>\r\n<td>&nbsp;周三</td>\r\n<td>&nbsp;</td>\r\n<td>&nbsp;</td>\r\n</tr>\r\n<tr>\r\n<td>&nbsp;周四</td>\r\n<td>&nbsp;</td>\r\n<td>&nbsp;</td>\r\n</tr>\r\n<tr>\r\n<td>&nbsp;周五</td>\r\n<td>&nbsp;</td>\r\n<td>&nbsp;</td>\r\n</tr>\r\n<tr>\r\n<td>&nbsp;周六</td>\r\n<td>&nbsp;</td>\r\n<td>&nbsp;</td>\r\n</tr>\r\n<tr>\r\n<td>&nbsp;周日</td>\r\n<td>&nbsp;</td>\r\n<td>&nbsp;</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p style=\"text-align:left;line-height:30pt;margin:0cm 0cm 0pt;mso-line-height-rule:exactly;\" class=\"MsoNormal\" align=\"left\"><span style=\"font-family:仿宋;color:black;font-size:16pt;mso-bidi-font-family:仿宋;\">一、密切掌握稳定动态。</span><span style=\"font-family:仿宋;font-size:16pt;mso-bidi-font-family:仿宋;\" lang=\"EN-US\"><?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /><o:p></o:p></span></p>\r\n<p style=\"margin:0cm 0cm 0pt;\" class=\"MsoNormal\"><span style=\"font-family:仿宋;color:black;font-size:16pt;mso-bidi-font-family:仿宋;\">二、认真接待来信来访，及时梳理群众诉求并按程序办理。<span lang=\"EN-US\"><o:p></o:p></span></span></p>\r\n<p style=\"margin:0cm 0cm 0pt;\" class=\"MsoNormal\"><span style=\"font-family:仿宋;color:black;font-size:16pt;mso-bidi-font-family:仿宋;\">三、完善信访突出问题挂牌督办包案化解方案。<span lang=\"EN-US\"><o:p></o:p></span></span></p>\r\n<p style=\"margin:0cm 0cm 0pt;\" class=\"MsoNormal\"><span style=\"font-family:仿宋;color:black;font-size:16pt;mso-bidi-font-family:仿宋;\">四、与业务处一道，开展溪洛渡电站移民信访复核工作；根据后扶处意见，完成二滩电站移民信访复核工作。<span lang=\"EN-US\"><o:p></o:p></span></span></p>\r\n<p style=\"margin:0cm 0cm 0pt;\" class=\"MsoNormal\"><span style=\"font-family:仿宋;color:black;font-size:16pt;mso-bidi-font-family:仿宋;\">五、到国土资源厅学习信访三级终结经验做法。<span lang=\"EN-US\"><o:p></o:p></span></span></p>\r\n<p style=\"margin:0cm 0cm 0pt;\" class=\"MsoNormal\"><span style=\"font-family:仿宋;color:black;font-size:16pt;mso-bidi-font-family:仿宋;\">六、完成局领导和上级机关交办工作。</span><span style=\"font-family:仿宋;font-size:16pt;mso-bidi-font-family:仿宋;\" lang=\"EN-US\"><o:p></o:p></span></p>\";s:9:\"startdate\";s:19:\"2014-10-08 08:30:00\";s:7:\"enddate\";s:19:\"2014-10-17 18:01:00\";s:5:\"otype\";s:0:\"\";s:10:\"department\";s:9:\"信访处\";s:13:\"participation\";s:50:\"姚立,兰素华,黄代利,曾磊,朱敏,赵燕玲\";s:6:\"person\";s:16:\"张谷,何智彬\";s:4:\"note\";s:0:\"\";s:4:\"date\";s:19:\"2014-10-08 09:08:22\";s:4:\"type\";s:6:\"部门\";s:3:\"uid\";s:2:\"82\";}','新增工作计划','2014-10-08 09:08:22','55','12');
